import React, { useEffect, useState, useRef } from "react"
import Peer from "simple-peer"
import io from "socket.io-client"

const hostname = "localhost:4000"
const socket = io(hostname)

const Homepage = () => {
  const videoRef = useRef(null)
  const [adminStream, adminSetStream] = useState("")

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        socket.emit("NewClient")
        adminSetStream(stream)
        videoRef.current.srcObject = stream
      })
      .catch((err) => console.log(err))
  }, [])

  // console.log(adminStream)

  return (
    <div>
      <h4> this is homepage</h4>

      <div class='col-12 col-sm-6 d-flex justify-content-center'>
        <div class='embed-responsive embed-responsive-16by9'>
          <video ref={videoRef} class='embed-responsive-item' muted>
            {}
          </video>
        </div>
      </div>

      <div class='col-12 col-sm-6 d-flex justify-content-center'>
        <div id='peerDiv' class='embed-responsive embed-responsive-16by9'></div>
      </div>
    </div>
  )
}

export default Homepage
