import React, { useEffect, useRef, useState } from "react"
import Peer from "simple-peer"
import io from "socket.io-client"

const hostname = "localhost:4000"
const socket = io(hostname)

const Homepage = () => {
  const videoRef = useRef(null)
  const peerVideoRef = useRef(null)
  const [sessionActive, setSessionActive] = useState("")
  //  const [adminStream, adminSetStream] = useState("")

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        socket.emit("NewClient")
        //  adminSetStream(stream)
        videoRef.current.srcObject = stream
        videoRef.current.play()
        console.log(videoRef.current)

        let client = {}

        function InitPeer(type) {
          console.log("peer received")
          let peer = new Peer({
            initiator: type === "init" ? true : false,
            stream: stream,
            trickle: false,
          })

          console.log("peer", peer)

          peer.on("stream", (stream) => {
            console.log("stream", stream)
            peerVideoRef.current.srcObject = stream
            peerVideoRef.current.play()
          })

          peer.on("close", () => {
            peerVideoRef.current.remove()
            peer.destroy()
            console.log("peer destroyed")
          })

          return peer
        }

        socket.on("CreatePeer", () => {
          client.gotAnswer = false

          let peer = InitPeer("init")
          peer.on("signal", (data) => {
            if (!client.gotAnswer) {
              socket.emit("offer", data)
            }
          })
          client.peer = peer
        })

        socket.on("backOffer", (offer) => {
          let peer = InitPeer("notInit")

          peer.on("signal", (data) => {
            socket.emit("Answer", data)
          })
          peer.signal(offer)
          client.peer = peer
        })

        socket.on("backAnswer", (answer) => {
          client.gotAnswer = true
          let peer = client.peer
          peer.signal(answer)
        })

        socket.on("SessionActive", () => {
          setSessionActive("Session is active , Please comeback later")
          videoRef.current.remove()
        })
      })
      .catch((err) => console.log(err))
  }, [])

  // console.log(adminStream)

  return (
    <div>
      <h4> Welcome to video chat</h4>
      <p>{sessionActive}</p>

      <div className='col-12 col-sm-6 d-flex justify-content-center d-inline'>
        <div className='embed-responsive embed-responsive-16by9'>
          <video ref={videoRef} className='embed-responsive-item' muted>
            {}
          </video>
        </div>
      </div>

      <div className='col-12 col-sm-6 d-flex justify-content-center d-inline'>
        <div id='peerDiv' className='embed-responsive embed-responsive-16by9'>
          <video ref={peerVideoRef} className='"embed-responsive-item"'></video>
        </div>
      </div>
    </div>
  )
}

export default Homepage
